import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { ListFilter, Plus, ChevronRight } from "lucide-react";
import DataTableFilterItem from "./data-table-filter-item";
import { useState } from "react";
import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
  DragEndEvent,
} from "@dnd-kit/core";
import {
  arrayMove,
  SortableContext,
  sortableKeyboardCoordinates,
  verticalListSortingStrategy,
} from "@dnd-kit/sortable";
import { Table } from "@tanstack/react-table";
import type { ExtendedColumnFilter } from "@/types/data-table";

// Generate a random ID
function generateId(length: number = 8): string {
  return Math.random().toString(36).substring(2, 2 + length);
}

export default function DataTableFilter<TData>({ table }: { table: Table<TData> }) {
  const [filters, setFilters] = useState<ExtendedColumnFilter<TData>[]>([
    {
      filterId: generateId(),
      id: "" as Extract<keyof TData, string>,
      value: "",
      operator: "iLike",
      variant: "text",
    },
  ]);
  const [logicalOperator, setLogicalOperator] = useState<"and" | "or">("and");

  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  const addFilter = () => {
    const newFilter: ExtendedColumnFilter<TData> = {
      filterId: generateId(),
      id: "" as Extract<keyof TData, string>,
      value: "",
      operator: "iLike",
      variant: "text",
    };
    setFilters([...filters, newFilter]);
  };

  const removeFilter = (filterId: string) => {
    setFilters(filters.filter((f) => f.filterId !== filterId));
  };

  const handleDragEnd = (event: DragEndEvent) => {
    const { active, over } = event;
    if (over && active.id !== over.id) {
      setFilters((items) => {
        const oldIndex = items.findIndex((item) => item.filterId === active.id);
        const newIndex = items.findIndex((item) => item.filterId === over.id);
        return arrayMove(items, oldIndex, newIndex);
      });
    }
  };

  const updateFilter = (filterId: string, newFilter: ExtendedColumnFilter<TData>) => {
    setFilters(
      filters.map((f) => (f.filterId === filterId ? newFilter : f))
    );
  };

  const applyFilters = () => {
    // Get valid filters (those with column selected and either have value or are empty/not empty operators)
    const validFilters = filters.filter((f) => 
      f.id && f.operator && (
        (f.value !== "" && f.value !== null && f.value !== undefined) || 
        ["isEmpty", "isNotEmpty"].includes(f.operator)
      )
    );

    // Convert to TanStack table format with custom filter functions
    const columnFilters = validFilters.map((filter) => {
      return {
        id: filter.id as string,
        value: {
          operator: filter.operator,
          value: filter.value,
          variant: filter.variant
        }
      };
    });

    table.setColumnFilters(columnFilters);
  };

  return (
    <Popover>
      <PopoverTrigger asChild>
        <Button variant="outline">
          <ListFilter className="h-4 w-4" />
          <div>Filter</div>
          <Badge variant="secondary">
            {filters.length}
          </Badge>
        </Button>
      </PopoverTrigger>
      <PopoverContent align="start" className="w-fit p-3">
        <div className="flex w-full flex-col gap-3">
          <p className="text-sm font-medium text-muted-foreground">
            Filter by:
          </p>
          <DndContext
            sensors={sensors}
            collisionDetection={closestCenter}
            onDragEnd={handleDragEnd}
          >
            <SortableContext
              items={filters.map((item) => item.filterId)}
              strategy={verticalListSortingStrategy}
            >
              <div className="flex flex-col gap-3">
                {filters.map((filter, index) => (
                  <DataTableFilterItem
                    key={filter.filterId}
                    table={table}
                    filter={filter}
                    onFilterChange={(newFilter) => updateFilter(filter.filterId, newFilter)}
                    onRemove={() => removeFilter(filter.filterId)}
                    index={index}
                    logicalOperator={logicalOperator}
                    onLogicalOperatorChange={setLogicalOperator}
                  />
                ))}
              </div>
            </SortableContext>
          </DndContext>

          <Separator />

          <div className="flex flex-row items-center justify-between gap-3">
            <Button
              variant="secondary"
              className="flex h-8 flex-row items-center gap-2"
              onClick={addFilter}
            >
              <Plus className="h-4 w-4" />
              Add
            </Button>
            <Button
              variant="default"
              className="flex h-8 flex-row items-center gap-2"
              onClick={applyFilters}
            >
              Apply
              <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </PopoverContent>
    </Popover>
  );
}

import { Table } from "@tanstack/react-table"
import {
  ChevronLeft,
  ChevronRight,
  ChevronsLeft,
  ChevronsRight,
} from "lucide-react"

import { Button } from "@/components/ui/button"
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select"

interface DataTablePaginationProps<TData> {
  table: Table<TData>
}

export function DataTablePagination<TData>({
  table,
}: DataTablePaginationProps<TData>) {
  return (
    <div className="flex items-center justify-between">
      <div className="text-muted-foreground flex-1 text-sm">
        {table.getFilteredSelectedRowModel().rows.length} of{" "}
        {table.getFilteredRowModel().rows.length}  selected.
      </div>
      <div className="flex items-center space-x-6 lg:space-x-8">
        <div className="flex items-center space-x-2">
          <p className="text-sm font-medium">Rows per page</p>
          <Select
            value={`${table.getState().pagination.pageSize}`}
            onValueChange={(value) => {
              table.setPageSize(Number(value))
            }}
          >
            <SelectTrigger className="h-8 w-[70px]">
              <SelectValue placeholder={table.getState().pagination.pageSize} />
            </SelectTrigger>
            <SelectContent side="top">
              {[10, 20, 25, 30, 40, 50].map((pageSize) => (
                <SelectItem key={pageSize} value={`${pageSize}`}>
                  {pageSize}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
        <div className="flex w-[100px] items-center justify-center text-sm font-medium">
          Page {table.getState().pagination.pageIndex + 1} of{" "}
          {table.getPageCount()}
        </div>
        <div className="flex items-center space-x-2">
          <Button
            variant="secondary"
            size="icon"
            className="hidden size-8 lg:flex"
            onClick={() => table.setPageIndex(0)}
            disabled={!table.getCanPreviousPage()}
          >
            <span className="sr-only">Go to first page</span>
            <ChevronsLeft />
          </Button>
          <Button
            variant="secondary"
            size="icon"
            className="size-8"
            onClick={() => table.previousPage()}
            disabled={!table.getCanPreviousPage()}
          >
            <span className="sr-only">Go to previous page</span>
            <ChevronLeft />
          </Button>
          <Button
            variant="secondary"
            size="icon"
            className="size-8"
            onClick={() => table.nextPage()}
            disabled={!table.getCanNextPage()}
          >
            <span className="sr-only">Go to next page</span>
            <ChevronRight />
          </Button>
          <Button
            variant="secondary"
            size="icon"
            className="hidden size-8 lg:flex"
            onClick={() => table.setPageIndex(table.getPageCount() - 1)}
            disabled={!table.getCanNextPage()}
          >
            <span className="sr-only">Go to last page</span>
            <ChevronsRight />
          </Button>
        </div>
      </div>
    </div>
  )
}

import { ChevronsUpDown, GripVertical, X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Command, CommandEmpty, CommandGroup, CommandInput, CommandItem, CommandList } from "@/components/ui/command";
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { Column } from "@tanstack/react-table";
import { useState } from "react";

interface DataTableSortItemProps<TData> {
  id: string;
  column?: string;
  direction: "asc" | "desc";
  onColumnChange: (id: string, column: string) => void;
  onDirectionChange: (id: string, direction: "asc" | "desc") => void;
  onRemove: (id: string) => void;
  columns: Column<TData, unknown>[];
}

export default function DataTableSortItem<TData>({
  id,
  column,
  direction,
  onColumnChange,
  onDirectionChange,
  onRemove,
  columns,
}: DataTableSortItemProps<TData>) {
  const [open, setOpen] = useState(false);
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
  };

  return (
    <div 
      ref={setNodeRef} 
      style={style}
      className="flex flex-row gap-2 items-center w-full"
    >
      <Button 
        variant="ghost" 
        size="icon"
        {...attributes}
        {...listeners}
        className="cursor-grab active:cursor-grabbing"
      >
        <GripVertical className="w-4 h-4" />
      </Button>

      <Popover open={open} onOpenChange={setOpen}>
        <PopoverTrigger asChild>
          <Button variant="outline" className="flex-1">
            <span className="flex-1 text-left capitalize">{column || "Column"}</span>
            <ChevronsUpDown className="w-4 h-4" />
          </Button>
        </PopoverTrigger>
        <PopoverContent align="start" className="p-0">
          <Command>
            <CommandInput placeholder="Search column" />
            <CommandList>
              <CommandEmpty>No results found.</CommandEmpty>
              <CommandGroup>
                {columns.map((col) => (
                  <CommandItem
                    key={col.id}
                    onSelect={() => {
                      onColumnChange(id, col.id);
                      setOpen(false);
                    }}
                    className="capitalize"
                  >
                    {col.id}
                  </CommandItem>
                ))}
              </CommandGroup>
            </CommandList>
          </Command>
        </PopoverContent>
      </Popover>

      <Select value={direction} onValueChange={(value: "asc" | "desc") => onDirectionChange(id, value)}>
        <SelectTrigger className="w-[5.25rem]">
          <SelectValue placeholder="Sort" />
        </SelectTrigger>
        <SelectContent>
          <SelectItem value="asc">Asc</SelectItem>
          <SelectItem value="desc">Desc</SelectItem>
        </SelectContent>
      </Select>

      <Button variant="ghost" size="icon" onClick={() => onRemove(id)}>
        <X className="w-4 h-4" />
      </Button>
    </div>
  );
}

import { ChevronRight, ChevronsUpDown, Plus } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { useState } from "react";
import DataTableSortItem from "./data-table-sort-item";
import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
  DragEndEvent,
} from '@dnd-kit/core';
import {
  arrayMove,
  SortableContext,
  sortableKeyboardCoordinates,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import { SortingState, Table } from "@tanstack/react-table";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";

interface SortItem {
  id: string;
  column?: string;
  direction: "asc" | "desc";
}

interface DataTableSortProps<TData> {
  table: Table<TData>;
}

export default function DataTableSort<TData>({ table }: DataTableSortProps<TData>) {
  const [sortItems, setSortItems] = useState<SortItem[]>([
    { id: "1", direction: "asc" }
  ]);

  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  const addSortItem = () => {
    const newId = Date.now().toString(); // Use timestamp for unique IDs
    setSortItems([...sortItems, { id: newId, direction: "asc" }]);
  };

  const removeSortItem = (id: string) => {
    setSortItems(sortItems.filter(item => item.id !== id));
  };

  const updateSortItemColumn = (id: string, column: string) => {
    setSortItems(sortItems.map(item => 
      item.id === id ? { ...item, column } : item
    ));
  };

  const updateSortItemDirection = (id: string, direction: "asc" | "desc") => {
    setSortItems(sortItems.map(item => 
      item.id === id ? { ...item, direction } : item
    ));
  };

  const handleDragEnd = (event: DragEndEvent) => {
    const { active, over } = event;

    if (over && active.id !== over.id) {
      setSortItems((items) => {
        const oldIndex = items.findIndex((item) => item.id === active.id);
        const newIndex = items.findIndex((item) => item.id === over.id);

        return arrayMove(items, oldIndex, newIndex);
      });
    }
  };

  const applySort = () => {
    const sorting: SortingState = sortItems
      .filter((item) => !!item.column)
      .map((item) => ({
        id: item.column!,
        desc: item.direction === "desc",
      }));
    table.setSorting(sorting);
  };

  const columns = table
    .getAllColumns()
    .filter(
      (column) =>
        typeof column.accessorFn !== "undefined" && column.getCanSort()
    );

  return (
    <Popover>
      <PopoverTrigger asChild>
        <Button variant="outline">
          <ChevronsUpDown className="w-4 h-4" />
          <div>Sort</div>
          <Badge variant="secondary">
            {sortItems.length}
          </Badge>
        </Button>
      </PopoverTrigger>
      <PopoverContent align="start" className="w-sm p-3">
        <div className="flex flex-col gap-3 w-full">
          <p className="text-sm font-medium text-muted-foreground">
            Sort by:
          </p>

          <DndContext
            sensors={sensors}
            collisionDetection={closestCenter}
            onDragEnd={handleDragEnd}
          >
            <SortableContext items={sortItems.map(item => item.id)} strategy={verticalListSortingStrategy}>
              {sortItems.map((item) => (
                <DataTableSortItem
                  key={item.id}
                  id={item.id}
                  column={item.column}
                  direction={item.direction}
                  onColumnChange={updateSortItemColumn}
                  onDirectionChange={updateSortItemDirection}
                  onRemove={removeSortItem}
                  columns={columns}
                />
              ))}
            </SortableContext>
          </DndContext>

          <Separator />

          <div className="flex flex-row gap-3 items-center justify-between">
            <Button variant="secondary" onClick={addSortItem} className="flex flex-row gap-2 items-center h-8">
              <Plus className="w-4 h-4" />
              Add
            </Button>
            <Button variant="default" onClick={applySort} className="flex flex-row gap-2 items-center h-8">
              Apply
              <ChevronRight className="w-4 h-4" />
            </Button>
          </div>
        </div>
      </PopoverContent>
    </Popover>
  );
}

